import { Address } from 'oasis-std';
export interface Policy<Params> {
    address: Address;
    /**
     * Checks whether the policy will accept a request from the current identity.
     *
     * If the optional `forceLocal` parameter is `true`, this method will throw if the
     * policy cannot be run locally.
     *
     * @returns whether the policy accepts a request from the connected identity with the
     *                  provided `parameters.
     */
    check(params: Params, forceLocal?: boolean): Promise<boolean>;
}
